import "./App.css";
import axios from "axios";
import React, { useRef, useState, useEffect } from "react";

const HandwritingApp = () => {
  const canvasRef = useRef(null);
  const contextRef = useRef(null);
  const [isDraw, setIsDraw] = useState(false);

  const [people, setPeople] = useState([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = 600;
    canvas.height = 600;

    const context = canvas.getContext("2d");
    context.lineCap = "round";
    context.strokeStyle = "black";
    context.lineWidth = 5;
    contextRef.current = context;

    axios
      .get("/api")
      .then((res) => setPeople(res.data))
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const startDraw = (e) => {
    contextRef.current.beginPath();
    contextRef.current.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    setIsDraw(true);
  };

  const finishDraw = () => {
    contextRef.current.closePath();
    setIsDraw(false);
  };

  const draw = (e) => {
    if (!isDraw) return;
    contextRef.current.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    contextRef.current.stroke();
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.clearRect(0, 0, canvas.width, canvas.height);
  };

  const sendImage = () => {
    const canvas = canvasRef.current;
    const imageData = canvas.toDataURL("image/png"); // Lấy dữ liệu ảnh dưới dạng base64

    axios
      .post("/upload", { image: imageData }) // Gửi dữ liệu ảnh lên backend
      .then((res) => console.log(res.data))
      .catch((error) => {
        console.error("Error sending image:", error);
      });
  };

  return (
    <div>
      <canvas
        ref={canvasRef}
        onMouseDown={startDraw}
        onMouseUp={finishDraw}
        onMouseMove={draw}
        onMouseLeave={finishDraw}
        onTouchStart={(e) => startDraw(e.touches[0])}
        onTouchEnd={finishDraw}
        onTouchMove={(e) => draw(e.touches[0])}
        style={{
          border: "2px solid #000",
          cursor: "crosshair",
        }}
      />

      <button
        onClick={clearCanvas}
        style={{
          position: "absolute",
          top: "300px",
          left: "1500px",
          zIndex: 10,
          padding: "15px 30px",
          fontSize: "20px",
          backgroundColor: "#007BFF",
          color: "#FFF",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Clear
      </button>

      <button
        onClick={sendImage}
        style={{
          position: "absolute",
          top: "400px", // Điều chỉnh vị trí nút
          left: "700px",
          zIndex: 10,
          padding: "15px 30px",
          fontSize: "20px",
          backgroundColor: "#28a745", // Màu xanh lá cây
          color: "#FFF",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Send Image
      </button>

      <ul>
        {people.map((p, index) => {
          return (
            <p key={index}>
              {p.id} {p.name} {p.age}
            </p>
          );
        })}
      </ul>
    </div>
  );
};

export default HandwritingApp;
